public Optional<Integer> getProjectIdByProjectShortname(String projectShortname) throws Exception
{
  ClientRequest request = new ClientRequest(LabcaseInformation.LABCASE_BASE_URL.concat("projects/").concat(projectShortname).concat(".json?include=journals&").concat("&key=").concat(apiKey.orElse("YOUR_API_KEY_HERE")));
  request.accept("application/json");
  ClientResponse<String> response = request.get(String.class);

  if (response.getStatus() != 200) {
    throw new RuntimeException("Failed : HTTP error code : "
                                   + response.getStatus());
  }

  JSONParser parser=new JSONParser();
  Object jsonResponse = parser.parse(response.getEntity());
  JSONObject resultObject = (JSONObject) jsonResponse;
  JSONObject projectObject = (JSONObject) resultObject.get("project");
  Long projectId = (Long) projectObject.get("id");

  return Optional.of(projectId.intValue());

}
