import PopupDialog

// Prepare the popup assets
let title = "THIS IS THE DIALOG TITLE"
let message = "This is the message section of the popup dialog default view"
let image = UIImage(named: "pexels-photo-103290")

// Create the dialog
let popup = PopupDialog(title: title, message: message, image: image)

// Create buttons
let buttonOne = CancelButton(title: "CANCEL") {
    print("You canceled the car dialog.")
}

// This button will not the dismiss the dialog
let buttonTwo = DefaultButton(title: "ADMIRE CAR", dismissOnTap: false) {
    print("What a beauty!")
}

let buttonThree = DefaultButton(title: "BUY CAR", height: 60) {
    print("Ah, maybe next time :)")
}

// Add buttons to dialog
// Alternatively, you can use popup.addButton(buttonOne)
// to add a single button
popup.addButtons([buttonOne, buttonTwo, buttonThree])

// Present dialog
self.present(popup, animated: true, completion: nil)

// Reset purchase button in TableView section header 
headerView.shoppingResetPurchasesButtonTapAction  = {
  () in
  let shoppingResetPurchasesDialogTitle = "Reset your purchases"
  let shoppingResetPurchasesDialogMessage = "Do you want to reset your purchases *now*"
  let popup = PopupDialog(title: shoppingResetPurchasesDialogTitle, message: shoppingResetPurchasesDialogMessage) //  + "\nEnjoy it for just \(product.localizedPrice ?? "-").
  let buttonOne = CancelButton(title: "CANCEL") {
    Log.debug?.message("Dialog closed.")
  }
  let buttonTwo = DefaultButton(title: "YES - RESTORE PURCHASES NOW") {
    Log.debug?.message("SettingsTableVC~viewForHeaderInSection - Restore purchases")
    IAPManager.shared.restorePreviousPurchases()
  }
  popup.addButtons([buttonOne, buttonTwo])
  self.present(popup, animated: true, completion: nil)
}

private func showDetailPurchaseDialogue(forCrap crap: Crap) {

  switch crap.isPurchased {
    case true:
      let popupMessage = package.detail + "\n\n" + "You already own this crap."
      let popup = PopupDialog(title: crap.name, message: popupMessage)
      let buttonOne = CancelButton(title: "OK") {
      }
      popup.addButtons([buttonOne])
      self.present(popup, animated: true, completion: nil)
      break
    default:
      let popup = PopupDialog(title: crap.name, message: crap.detail)
      let buttonOne = CancelButton(title: "OK") {
      }

      let buttonTwo = DefaultButton(title: "Get your device full of fishy crabs.") {
        self.buyProduct(byProductId: crap.productId)
      }
      popup.addButtons([buttonOne, buttonTwo])
      self.present(popup, animated: true, completion: nil)
    }
}
