override func prepare(for segue: UIStoryboardSegue, sender: Any?) {

  if segue.identifier == "SegueIdentifierFromStoryboard" {
    if let segueDestination = segue.destination as? MySubViewController, let index = collectionView.indexPathsForSelectedItems?.first {
      segueDestination.selection = myData[index.row]
    }
  }
}
